$(document).ready(function(){		
	$("a").mouseenter(function(){
		$(".navbar-brand").css("color", "#FFFF00");
	});
	
	$("a").mouseleave(function(){
		$(".navbar-brand").css("color", "#FFFFFF");
	});	
	
	$("#current").css('background-color','#31B404');
	
	$('.nav-link').css('color','#FFFFFF');		
});
			